L0 = sqrt(4^2 + 6^2)L1 = sqrt(6^2 + 13^2)L2 = sqrt(12^2 + 5^2)L3 = sqrt(10^2 + 9^2)ox1 = 2oy1 = 7ox2 = 8oy2 = -1ox3 = 6oy3 = 3M = [ 2*(L1-L0), -2*ox1, -2*oy1;       2*(L2-L0), -2*ox2, -2*oy2;      2*(L3-L0), -2*ox3, -2*oy3]      b = [  -(L1-L0)^2 + ox1^2 + oy1^2,  -(L2-L0)^2 + ox2^2 + oy2^2,  -(L3-L0)^2 + ox3^2 + oy3^2]    a1 = inv(M) * bR0 = sqrt(11^2 + 6^2)R1 = sqrt(9^2 + 13^2)R2 = sqrt(3^2 + 5^2)or1 = R1-R0or2 = R2-R0x0 = a1(2)y0 = a1(3)M2 = [  -2*ox1, -2*or1;  -2*ox2, -2*or2]b2 = [  x0^2 + or1^2 - (x0 + ox1)**2 - 2*y0*oy1 - oy1^2,  x0^2 + or2^2 - (x0 + ox2)**2 - 2*y0*oy2 - oy2^2]a2 = inv(M2) * b2